mapper.select:
SELECT QST_SEQ, QST_ID, QST_TITLE, QST_CONTENT, QST_STATUS, QST_REGDATE, QST_FAST, USERS_NAME, QST_SPECIES, ANM_SPECIES, QST_PART, PART_NAME FROM (
SELECT QST_SEQ, QST_ID, QST_TITLE, QST_CONTENT, QST_STATUS, QST_REGDATE, QST_FAST, USERS_NAME, QST_SPECIES, ANM_SPECIES, QST_PART, PART_NAME, ROW_NUMBER() OVER(ORDER BY QST_FAST DESC, QST_REGDATE DESC) rn FROM QUESTBOARD q JOIN USERSINFO u ON q.QST_ID = u.USERS_ID JOIN ANIMALCODE a1 ON q.QST_SPECIES = a1.ANM_CODE JOIN ANIMALPART a2 ON q.QST_PART = a2.PART_CODE
WHERE QST_STATUS != 'N' )
AND qst_species = #{qst_species}
AND qst_part = #{qst_part}
AND qst_content LIKE '%'||#{qst_content}||'%'
AND rn BETWEEN #{first} AND #{last}
ORDER BY QST_FAST DESC, QST_REGDATE DESC

mapper.update:
UPDATE QUESTBOARD
SET QST_FAST='Y'
WHERE QST_SEQ=#{qst_seq}

UPDATE QUESTBOARD
SET QST_CONTENT=#{qst_content}, QST_TITLE=#{qst_title}, QST_SPECIES=#{qst_species}, QST_PART=#{qst_part}
WHERE QST_SEQ=#{qst_seq}

UPDATE QUESTBOARD
SET QST_STATUS ='R'
WHERE QST_SEQ=#{qst_seq}

UPDATE QUESTBOARD
SET QST_STATUS ='N'
WHERE QST_SEQ=#{qst_seq}

UPDATE REPLYBOARD
SET RPY_CONTENT=#{rpy_content}
WHERE RPY_SEQ = #{rpy_seq}

UPDATE REPLYBOARD 
SET RPY_STATUS ='N'
WHERE RPY_SEQ = #{rpy_seq}

UPDATE REPLYBOARD 
SET RPY_STATUS ='R'
WHERE RPY_SEQ = #{rpy_seq}

UPDATE REPLYBOARD 
SET RPY_CHOSEN ='Y'
WHERE RPY_SEQ = #{rpy_seq}

mapper.select:
SELECT ANM_CODE, ANM_SPECIES
	FROM ANIMALCODE
	WHERE ANM_CODE = ${anm_code}

SELECT PART_CODE, PART_NAME
	FROM ANIMALPART
	WHERE PART_CODE = ${part_code}

SELECT QST_SEQ, QST_ID, QST_TITLE, QST_CONTENT, QST_STATUS, QST_REGDATE, QST_FAST, USERS_NAME, QST_SPECIES, ANM_SPECIES, QST_PART, PART_NAME FROM (
SELECT QST_SEQ, QST_ID, QST_TITLE, QST_CONTENT, QST_STATUS, QST_REGDATE, QST_FAST, USERS_NAME, QST_SPECIES, ANM_SPECIES, QST_PART, PART_NAME, ROW_NUMBER() OVER(ORDER BY QST_FAST DESC, QST_REGDATE DESC) rn FROM QUESTBOARD q JOIN USERSINFO u ON q.QST_ID = u.USERS_ID JOIN ANIMALCODE a1 ON q.QST_SPECIES = a1.ANM_CODE JOIN ANIMALPART a2 ON q.QST_PART = a2.PART_CODE
QST_STATUS != 'N'
AND qst_species = #{qst_species}
AND qst_part = #{qst_part}
AND qst_content LIKE '%'||#{qst_content}||'%'
)
rn BETWEEN #{first} AND #{last}
ORDER BY QST_FAST DESC, QST_REGDATE DESC

  	SELECT COUNT(*) FROM QUESTBOARD

  	QST_STATUS != 'N'

	AND qst_species = #{qst_species}


	AND qst_part = #{qst_part}


	AND qst_content LIKE '%'||#{qst_content}||'%'
